# This file should contain all the record creation needed to seed the database with its default values.
# The data can then be loaded with the rails db:seed command (or created alongside the database with db:setup).
#
# Examples:
#
#   movies = Movie.create([{ name: 'Star Wars' }, { name: 'Lord of the Rings' }])
#   Character.create(name: 'Luke', movie: movies.first)
require 'open-uri'
require 'json'

puts 'initializing the database'

puts 'destroying previous ingredients'
Ingredient.destroy_all

puts 'creating all the ingredients'
url = 'https://www.thecocktaildb.com/api/json/v1/1/list.php?i=list'
user_serialized = open(url).read
user = JSON.parse(user_serialized)
array = user['drinks']

array.each do |hash|
  Ingredient.create!(name: hash['strIngredient1'])
end

puts 'all ingredients created, the mixing begins'

puts 'creating cocktails'

Cocktail.create!(name: 'Old Fashioned', image: "data:image/jpeg;base64,")
Cocktail.create!(name: "Moscow Mule", image: "data:image/jpeg;base64,")
Cocktail.create!(name: "Mojito", image: "data:image/jpeg;base64,")

puts 'cocktails created'
